targets.expr:
sum by (workflowName) (
  increase(
    platform_engine_workflow_completed_time_seconds_count{}[2m]
  )
) / (
  sum by (workflowName) (
    increase(
      platform_engine_workflow_completed_time_seconds_count{}[2m]
    ) or platform_engine_workflow_count{} * 0
  ) + sum by (workflowName) (
    increase(
      platform_engine_workflow_earlyexit_time_seconds_count{}[2m]
    ) or platform_engine_workflow_count{} * 0
  ) + sum by (workflowName) (
    increase(
      platform_engine_workflow_error_time_seconds_count{}[2m]
    ) or platform_engine_workflow_count{} * 0
  ) + sum by (workflowName) (
    increase(
      platform_engine_workflow_timeout_time_seconds_count{}[2m]
    ) or platform_engine_workflow_count{} * 0
  )
)

